  //     const response = await axios.post("/process-matching", "");
  //     console.log(response.status);
  //   } catch (error) {
  //     console.log(response.error);
  //   }
  // };

  const handleClick = () => {
    dispatch(click(isMatched));
    const jd = { job_name: jobName, job_description: jobDescription };
  };
  const postData = async () => {
    try {
      // request jobname {'job_name' : name} || request jobdescription {"job_name": name, " job_description": text}
      const job_response = await axios.post("/process-matching", job_name);
      const jd_response = await axios.post("/job", job_description); // cái này là submit riêng
      console.log(job_response.status);
      console.log(jd_response.status);
    } catch (error) {
      console.log(job_response.error);
      console.log(jd_response.error);
    }
  };

  return (
    <div className="match-container">
      <button className="match-button" onClick={handleClick}>
        Match
      </button>
    </div>
  );
}

export default Match;
