import { createSlice } from "@reduxjs/toolkit";
import resumeSlice from "./resumeSlice";

const matchSlice = createSlice({
  name: "match",
  initialState: {
    isMatched: false,
  },
  reducers: {
    click: (state, action) => {
      return { ...state, isMatched: !isMatched };
    },
  },
});

export const { click } = resumeSlice.action;

export default resumeSlice.reducers;
